import string
number=list(range(1,26))
alphabets=list(string.ascii_lowercase)
punctuation=list(string.punctuation)
ws=list(string.whitespace)

def encode(str1):
    ls=[]
    l=len(str1)
    for i in range(0,l):
        for j in range(0,25):
            if str1.lower()[i]==alphabets[j]:
                x=str(number[j])
                if(i!=l-1):
                    ls.append(x+'.')
                else:
                    ls.append(x)           
        if str1[i] in punctuation or str1[i] in ws: 
            if(i!=l-1):
                ls.append('999.')
            else:
                ls.append('999')
    ls_str = ''.join(ls)
    print(ls_str)

def get_n(threshold):
    harmonic_sum=0
    count=1
    while True:
        harmonic_sum=harmonic_sum+(1/count)
        count += 1
        if harmonic_sum >=threshold:
            break
    print(count-1)
